// Extract and process WhatsApp message data
const webhookData = $input.first().json;
const entry = webhookData.entry?.[0];
const messageData = entry?.changes?.[0]?.value?.messages?.[0];
const contactData = entry?.changes?.[0]?.value?.contacts?.[0];

if (!messageData) {
  return [{ json: { error: 'No message data found' } }];
}

// Extract message details
const messageText = messageData.text?.body || '';
const fromNumber = messageData.from;
const messageId = messageData.id;
const timestamp = messageData.timestamp;
const messageType = messageData.type;

// Extract contact info if available
const customerName = contactData?.profile?.name || 'Customer';

// Clean and analyze message
const cleanMessage = messageText.toLowerCase().trim();

// Determine message intent
let intent = 'general';
if (cleanMessage.includes('find') || cleanMessage.includes('search') || cleanMessage.includes('show')) {
  intent = 'search';
} else if (cleanMessage.includes('book') || cleanMessage.includes('reserve') || cleanMessage.includes('table')) {
  intent = 'booking';
} else if (cleanMessage.includes('help') || cleanMessage.includes('menu') || cleanMessage.includes('what')) {
  intent = 'help';
} else if (cleanMessage.includes('italian') || cleanMessage.includes('chinese') || cleanMessage.includes('japanese') || cleanMessage.includes('sushi')) {
  intent = 'cuisine_search';
}

// Detect cuisine preferences
const cuisines = [];
if (cleanMessage.includes('italian')) cuisines.push('italian');
if (cleanMessage.includes('chinese')) cuisines.push('chinese');
if (cleanMessage.includes('japanese') || cleanMessage.includes('sushi')) cuisines.push('japanese');
if (cleanMessage.includes('mexican')) cuisines.push('mexican');
if (cleanMessage.includes('indian')) cuisines.push('indian');

// Create formatted request for restaurant agent
const restaurantRequest = {
  entry: [{
    id: entry.id || 'n8n-generated',
    changes: [{
      value: {
        messaging_product: 'whatsapp',
        metadata: {
          display_phone_number: entry.changes?.[0]?.value?.metadata?.display_phone_number || '',
          phone_number_id: entry.changes?.[0]?.value?.metadata?.phone_number_id || ''
        },
        contacts: contactData ? [contactData] : [],
        messages: [{
          id: messageId,
          from: fromNumber,
          timestamp: timestamp,
          type: messageType,
          text: {
            body: messageText
          }
        }]
      }
    }]
  }]
};

// Return processed data
return [{
  json: {
    originalWebhook: webhookData,
    processedMessage: {
      messageText: messageText,
      cleanMessage: cleanMessage,
      fromNumber: fromNumber,
      customerName: customerName,
      messageId: messageId,
      timestamp: timestamp,
      intent: intent,
      cuisines: cuisines,
      restaurantRequest: restaurantRequest
    }
  }
}];

// Process successful restaurant agent response
const apiResponse = $input.first().json;
const originalData = $('Process WhatsApp Message').first().json;

// Extract response data
const responseBody = typeof apiResponse.body === 'string' ? 
  JSON.parse(apiResponse.body) : apiResponse.body;

// Get customer info
const fromNumber = originalData.processedMessage.fromNumber;
const customerName = originalData.processedMessage.customerName;
const messageId = originalData.processedMessage.messageId;

// Format WhatsApp response message
let whatsappMessage = '';

if (responseBody.restaurants && responseBody.restaurants.length > 0) {
  whatsappMessage = `üçΩÔ∏è Hi ${customerName}! I found ${responseBody.restaurants.length} restaurant${responseBody.restaurants.length > 1 ? 's' : ''} for you:\n\n`;

  responseBody.restaurants.forEach((restaurant, index) => {
    const stars = '‚≠ê'.repeat(Math.floor(restaurant.rating || 4));
    const priceLevel = 'üí∞'.repeat(restaurant.priceLevel || 2);

    whatsappMessage += `${index + 1}. **${restaurant.name}**\n`;
    whatsappMessage += `   üìç ${restaurant.address}\n`;
    whatsappMessage += `   üç¥ ${restaurant.cuisine?.join(', ') || 'Various'} ‚Ä¢ ${priceLevel}\n`;
    whatsappMessage += `   ${stars} ${restaurant.rating || 'N/A'}/5\n`;

    if (restaurant.openingHours?.weekdayText) {
      const today = new Date().getDay();
      const todayHours = restaurant.openingHours.weekdayText[today === 0 ? 6 : today - 1];
      whatsappMessage += `   üïê ${todayHours}\n`;
    }

    whatsappMessage += '\n';
  });

  whatsappMessage += 'To book a table, just say: "Book at [Restaurant Name] for [number] people on [date] at [time]"\n\n';
  whatsappMessage += 'Need more info? Ask me about specific restaurants! ü§ñ';
} else {
  whatsappMessage = `Hi ${customerName}! üòä\n\nI couldn't find any restaurants matching your request right now. \n\nTry asking for:\n‚Ä¢ "Italian restaurants"\n‚Ä¢ "Chinese food"\n‚Ä¢ "Japanese sushi"\n‚Ä¢ "Show me all restaurants"\n\nI'm here to help! üçΩÔ∏è`;
}

// Create WhatsApp API payload
const whatsappPayload = {
  messaging_product: 'whatsapp',
  to: fromNumber,
  type: 'text',
  text: {
    body: whatsappMessage
  }
};

return [{
  json: {
    success: true,
    fromNumber: fromNumber,
    customerName: customerName,
    messageId: messageId,
    responseMessage: whatsappMessage,
    whatsappPayload: whatsappPayload,
    restaurantData: responseBody
  }
}];

// Handle API error response
const apiResponse = $input.first().json;
const originalData = $('Process WhatsApp Message').first().json;

const fromNumber = originalData.processedMessage.fromNumber;
const customerName = originalData.processedMessage.customerName;

// Create error response
const errorMessage = `Hi ${customerName}! üòî\n\nI'm experiencing some technical difficulties right now. Please try again in a few moments.\n\nIf the problem persists, you can:\n‚Ä¢ Try a simpler request like "Italian restaurants"\n‚Ä¢ Contact our support team\n\nSorry for the inconvenience! üõ†Ô∏è`;

// Create WhatsApp API payload
const whatsappPayload = {
  messaging_product: 'whatsapp',
  to: fromNumber,
  type: 'text',
  text: {
    body: errorMessage
  }
};

return [{
  json: {
    success: false,
    error: true,
    fromNumber: fromNumber,
    customerName: customerName,
    responseMessage: errorMessage,
    whatsappPayload: whatsappPayload,
    apiError: apiResponse
  }
}];
